mapper.select:
select temp.* from (SELECT f.pay_id payId, f.pay_amt payAmt, f.finance_start_date financeStartDate, f.finance_end_date financeEndDate, f.finance_balance financeBalance, COALESCE(tempCar.counts,0) counts, COALESCE(r.interest_sum,0) interestSum, COALESCE(ci.agency_num,0) agencyNum, COALESCE(ci.corp_name,0) corpName from finance_info f LEFT JOIN (select COUNT(0) counts,c.finance_id from car_info c where c.sale_status = 0 GROUP BY c.finance_id) tempCar ON f.finance_id = tempCar.finance_id LEFT JOIN car_info car ON f.finance_id = car.finance_id inner JOIN revenue_management r ON r.finance_id = f.finance_id LEFT JOIN corp_info ci ON ci.corp_id = f.corp_id where car.sale_status = 0 and f.corp_id != 'corp00003'
and DATE_FORMAT(f.finance_end_date,'%Y-%m-%d') <= #{repaymentDate}
and ci.agency_num LIKE CONCAT(CONCAT('%', #{agencyNum}),'%')
and ci.corp_name LIKE CONCAT(CONCAT('%', #{corpName}),'%')
and ci.corp_id = #{corpId}
GROUP BY r.finance_id) temp

SELECT ci.agency_num agencyNum, ci.corp_name corpName, f.pay_id payId, f.pay_amt payAmt, f.finance_start_date financeStartDate, f.finance_end_date financeEndDate, f.finance_balance financeBalance, COALESCE(tempCar.counts,0) counts, COALESCE(r.interest_sum,0) interestSum, COALESCE(car.car_frame_num,0) carFrameNum, COALESCE(car.car_actual_price,0) carActualPrice FROM finance_info f LEFT JOIN (select COUNT(0) counts,c.finance_id from car_info c where c.sale_status = 0 GROUP BY c.finance_id) tempCar ON f.finance_id = tempCar.finance_id LEFT JOIN car_info car ON f.finance_id = car.finance_id inner JOIN revenue_management r ON r.finance_id = f.finance_id LEFT JOIN corp_info ci ON ci.corp_id = f.corp_id where car.sale_status = 0 and f.corp_id != 'corp00003'
and DATE_FORMAT(f.finance_end_date,'%Y-%m-%d') <= #{repaymentDate}
and ci.agency_num = #{agencyNum}
and ci.corp_name = #{corpName}
and ci.corp_id = #{corpId}
ORDER BY ci.corp_id,f.finance_id
